{
    "tosca_definitions_version": "tosca_simple_yaml_1_1_0",
    "topology_template": {
        "policies": [
            {
                "onap.policies.drools.vnf.Example": {
                    "type": "onap.policies.controlloop.operational.common.Drools",
                    "type_version": "1.0.0",
                    "version": "1.0.0",
                    "name": "onap.policies.drools.vnf.Example",
                    "properties": {
                        "id": "ControlLoop-vnf-d0a1dfc6-94f5-4fd4-a5b5-4630b438831b",
                        "timeout": 1200,
                        "abatement": false,
                        "trigger": "vnf-modifyConfig",
                        "operations": [
                            {
                                "id": "vnf-modifyConfig",
                                "description": "Test policy for vnf modify config",
                                "operation": {
                                    "actor": "CDS",
                                    "operation": "modify-config",
                                    "target": {
                                        "targetType": "VNF",
                                        "entityIds": {
                                            "resourceID": "vTEST_XYZ"
                                        }
                                    },
                                    "payload": {
                                        "artifact_name": "vnf-test-cba",
                                        "artifact_version": "1.0.0",
                                        "data": "{\"test\": \"test\"}"
                                    }
                                },
                                "timeout": 300,
                                "retries": 0,
                                "success": "vnf-restart",
                                "failure": "final_failure",
                                "failure_timeout": "final_failure_timeout",
                                "failure_retries": "final_failure_retries",
                                "failure_exception": "final_failure_exception",
                                "failure_guard": "final_failure_guard"
                            },
                            {
                                "id": "vnf-restart",
                                "description": "Test policy for vnf restart",
                                "operation": {
                                    "actor": "CDS",
                                    "operation": "restart",
                                    "target": {
                                        "targetType": "VNF",
                                        "entityIds": {
                                            "resourceID": "vTEST_XYZ"
                                        }
                                    },
                                    "payload": {
                                        "artifact_name": "vnf-test-cba",
                                        "artifact_version": "1.0.0",
                                        "data": "{\"test\": \"test\"}"
                                    }
                                },
                                "timeout": 300,
                                "retries": 0,
                                "success": "final_success",
                                "failure": "final_failure",
                                "failure_timeout": "final_failure_timeout",
                                "failure_retries": "final_failure_retries",
                                "failure_exception": "final_failure_exception",
                                "failure_guard": "final_failure_guard"
                            }
                        ],
                        "controllerName": "usecases"
                    }
                }
            }
        ]
    }
}
